import * as React from 'react';

function useStringHook() {
	return {
		capitolizeFirst(str) {
			console.log(str);
			let stringArr = str.split('');
			stringArr.toLowerCase();
			return stringArr[0].toUpperCase().join('');
		},
	};
}

export default useStringHook;
