import React from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function SignUp() {
	let navigate = useNavigate();

	const [msg, setMsg] = React.useState({
		text: '',
		success: false,
	});

	const [signUpData, setSignUpData] = React.useState({
		userName: '',
		email: '',
		password: '',
		confirmPassword: '',
	});

	function handleFormChange(event) {
		const { name, value, type, checked } = event.target;
		setSignUpData(prevSignUpData => ({
			...prevSignUpData,
			[name]: type === 'checkbox' ? checked : value,
		}));
	}
	// const handleSubmit = async event => {
	// 	event.preventDefault();
	// 	console.log(signUpData, 'Sign Up Attempt Sent');
	// 	try {
	// 		const response = await fetch('/signup', {
	// 			method: 'POST',
	// 			headers: { 'Content-Type': 'application/json' },
	// 			body: JSON.stringify(signUpData),
	// 			withCredentials: true,
	// 		});
	// 		const data = await response.json();
	// 		console.log('From Server:', data, data.msgBody);
	// 		setMsg(prevMsg => ({
	// 			...prevMsg,
	// 			text: data.message.msgBody,
	// 			success: true,
	// 		}));
	// 	} catch (err) {
	// 		console.log(err);
	// 	}
	// 	// navigate('/dashboard');
	// };

	const register = async event => {
		event.preventDefault();
		console.log(signUpData, 'Login Attempt Sent');
		try {
			const response = await axios({
				method: 'POST',
				data: { signUpData },
				url: 'http://localhost:5000/signup',
				withCredentials: true,
			});
			console.log('From Server:', response);
			setMsg(prevMsg => ({
				...prevMsg,
				text: response.message.msgBody,
				success: true,
			}));
		} catch (err) {
			setMsg(prevMsg => ({
				...prevMsg,
				text: err.response.data.message,
				success: true,
			}));
			// console.log(msg);
			console.log(err.response);
		}
	};

	return (
		<div>
			<section className='flex flex-col items-center p-10'>
				<div className='card w-96 shadow-xl bg-neutral'>
					<div className='card-body'>
						<h1 className='card-title self-center mb-4 text-white'>SignUp</h1>
						<form onSubmit={register} className='flex flex-col gap-2'>
							<input
								type='text'
								name='userName'
								placeholder='userName'
								onChange={handleFormChange}
								className='input input-bordered w-full max-w-xs'
							/>
							<input
								type='text'
								name='email'
								placeholder='Email'
								onChange={handleFormChange}
								className='input input-bordered w-full max-w-xs'
							/>
							<input
								type='password'
								name='password'
								placeholder='Password'
								onChange={handleFormChange}
								className='input input-bordered w-full max-w-xs'
							/>
							<input
								type='password'
								name='confirmPassword'
								placeholder='Confirm Password'
								onChange={handleFormChange}
								className='input input-bordered w-full max-w-xs'
							/>
							<div className='card-actions justify-center mt-4'>
								<button className='btn btn-primary'>Create User</button>
							</div>
						</form>
						<div className='text-success text-center'>
							{msg ? msg.text : ''}
						</div>
					</div>
				</div>
			</section>
		</div>
	);
}

export default SignUp;
