import React from 'react';
import { NavLink, useLocation } from 'react-router-dom';
import useAuth from './useAuth';
import Nav from '../components/Nav';

export const RequireAuth = ({ children }) => {
	const { authed, user, handleLogout } = useAuth();

	const location = useLocation();

	// return authed === true ? (
	// 	children
	// ) : (
	// 	<Navigate to='/' replace state={{ path: location.pathname }} />
	// );
	return (
		<div>
			{location.pathname === '/' && <Nav />}
			<div className='drawer drawer-mobile'>
				<input id='drawer' type='checkbox' className='drawer-toggle' />
				<div className='drawer-content flex flex-col'>
					{/* <!-- Page content here --> */}
					{authed ? (
						<div>{children}</div>
					) : (
						<div>
							<button className='btn loading'>loading</button>
						</div>
					)}
					<label
						htmlFor='drawer'
						className='btn btn-square drawer-button lg:hidden fixed top-2 left-2 z-50'
					>
						<svg
							xmlns='http://www.w3.org/2000/svg'
							fill='none'
							viewBox='0 0 24 24'
							strokeWidth={1.5}
							stroke='currentColor'
							className='w-6 h-6'
						>
							<path
								strokeLinecap='round'
								strokeLinejoin='round'
								d='M3.75 6.75h16.5M3.75 12h16.5m-16.5 5.25h16.5'
							/>
						</svg>
					</label>
				</div>
				<div className='drawer-side'>
					<label htmlFor='drawer' className='drawer-overlay'></label>
					<div className='menu p-4 overflow-y-auto bg-base-100 text-base-content w-64 flex flex-col gap-5'>
						{/* <!-- Sidebar content here --> */}

						<h2 className='font-bold text-center text-xl'>
							<NavLink to='/'>My Music Studio</NavLink>
						</h2>

						<ul>
							<li>
								<NavLink to='/dashboard'>Dashboard</NavLink>
							</li>
							<li>
								<NavLink to='/students'>Students</NavLink>
							</li>
						</ul>
						<div>
							<div className='flex flex-col'>
								<span>Authenticated as {user.userName}</span>
								<span>ID: {user._id}</span>
							</div>
							<button
								type='button'
								onClick={handleLogout}
								className='btn btn-neutral'
							>
								Sign Out
							</button>
						</div>
					</div>
				</div>
			</div>
		</div>
	);
};
