import React from 'react';
import useAuth from '../auth/useAuth';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function StudentTable({ currentStudent, setCurrentStudent }) {
	const { user } = useAuth();
	let navigate = useNavigate();

	const [students, setStudents] = React.useState([]);

	React.useEffect(() => {
		(async () => {
			try {
				const response = await axios({
					method: 'GET',
					data: user._id,
					url: 'http://localhost:5000/students',
					withCredentials: true,
				});
				setStudents(response.data);
			} catch (err) {
				console.log(err);
			}
		})();
	}, [user._id]);

	const studentArr = students
		.sort((a, b) => (a.name > b.name ? 1 : -1))
		.map((el, i) => {
			return (
				<div
					key={i}
					className='flex justify-evenly'
					onClick={() => {
						setCurrentStudent(el);
						navigate(`/students/studentprofile`);
					}}
				>
					<span>{el.name}</span>
					<span>{el.instrument}</span>
					<span>{el.email}</span>
				</div>
				// <tr
				// 	key={i}
				// 	onClick={() => {
				// 		setCurrentStudent(el);
				// 		navigate(`/students/studentprofile`);
				// 	}}
				// >
				// 	<td>
				// 		<div className='flex items-center space-x-3'>
				// 			<div>
				// 				<div className='font-bold'>{el.name}</div>
				// 			</div>
				// 		</div>
				// 	</td>
				// 	<td>{el.instrument}</td>
				// 	<td>{el.email}</td>
				// </tr>
			);
		});

	return (
		<div className='w-full border rounded-t-xl'>
			<div className='flex justify-between'>
				<h5>Name</h5>
				<h5>Instrument</h5>
				<h5>Phone</h5>
				<h5>Email</h5>
			</div>

			{studentArr}
		</div>
		// <div className='overflow-x-auto w-full border rounded-t-xl'>
		// 	<table className='table w-full'>
		// 		<thead>
		// 			<tr>
		// 				<th>Name</th>
		// 				<th>Instrument</th>
		// 				<th>Email</th>
		// 			</tr>
		// 		</thead>
		// 		<tbody>{studentArr}</tbody>
		// 	</table>
		// </div>
	);
}

export default StudentTable;
