import React from 'react';

const ProtectedRoute = ({ children }) => {
	const { token } = useAuth();
	const location = useLocation();

	if (!token) {
		return <Navigate to='/home' replace state={{ from: location }} />;
	}

	return children;
};

export default ProtectedRoute;
