import * as React from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { getAuthenticated } from '../../../controllers/auth';

const authContext = React.createContext();

function useAuth() {
	const [authed, setAuthed] = React.useState(null);
	const [user, setUser] = React.useState({});
	const navigate = useNavigate();

	React.useEffect(() => {
		(async () => {
			try {
				const response = await axios({
					method: 'GET',
					url: 'http://localhost:5000/authenticated',
					withCredentials: true,
				});
				console.log('From Server:', response);
				if (response.data._id) {
					setAuthed(true);
					setUser(response.data);
				} else {
					setAuthed(false);
					setUser({});
				}
			} catch (err) {
				console.log(err);
			}
		})();
	}, []);

	return {
		authed,
		user,
		handleLogin(user) {
			setAuthed(true);
			setUser(user);
		},
		async handleLogout() {
			try {
				const response = await axios({
					method: 'GET',
					url: 'http://localhost:5000/logout',
					withCredentials: true,
				});
				console.log('From Server:', response.data.message.msgBody);
				setAuthed(false);
				setUser(null);
				navigate('/');
			} catch (err) {
				console.log(err);
			}
		},
	};
}

export function AuthProvider({ children }) {
	const auth = useAuth();

	//return <authContext.Provider value={auth}>{children}</authContext.Provider>
	return (
		<div>
			{/* If app is loaded, we are passing the user and isAuthenticated values as a global state */}
			{!authed ? (
				<h1>Loading...</h1>
			) : (
				<authContext.Provider value={auth}>{children}</authContext.Provider>
			)}
		</div>
	);
}

export default function AuthConsumer() {
	return React.useContext(authContext);
}
