import React from 'react';
import axios from 'axios';
import SmallAddButton from '../components/SmallAddButton';
import useStudents from '../contexts/useStudents';
import useStringHook from '../hooks/useStringHook';
import DeleteButton from './DeleteButton';
import SmallCheckButton from './SmallCheckButton';

function ArrayInput({ student, arrayName }) {
	const { getStudents, setGetStudents } = useStudents();
	const [formData, setFormData] = React.useState({});
	const [show, setShow] = React.useState(false);
	const { capitolizeFirst } = useStringHook();

	React.useEffect(() => {
		setFormData({
			[arrayName]: '',
		});
	}, [arrayName]);

	async function handleDelete(i) {
		student[arrayName].splice(i, 1);
		try {
			const response = await axios({
				method: 'PUT',
				data: {
					id: student._id,
					[arrayName]: student[arrayName],
				},
				url: 'http://localhost:5000/students/updateStudent',
				withCredentials: true,
			});
			console.log(response);
			setGetStudents(!getStudents);
		} catch (err) {
			console.log(err);
		}
	}

	function handleFormChange(event) {
		const { name, value } = event.target;
		setFormData({ [name]: value });
		console.log(formData);
	}

	const handleSubmit = async event => {
		event.preventDefault();
		student[arrayName].push(formData[arrayName]);
		try {
			const response = await axios({
				method: 'PUT',
				data: {
					id: student._id,
					[arrayName]: student[arrayName],
				},
				url: 'http://localhost:5000/students/updateStudent',
				withCredentials: true,
			});
			console.log(response);
			setGetStudents(!getStudents);
		} catch (err) {
			console.log(err);
		}
	};

	return (
		<form onSubmit={handleSubmit}>
			<div className='flex gap-3 items-center'>
				<h5 className='font-semibold'>{capitolizeFirst(arrayName)}</h5>
				<div onClick={() => setShow(!show)}>
					<SmallAddButton />
				</div>
			</div>
			<ul className='list-disc list-inside'>
				{student[arrayName]?.length > 0 ? (
					student[arrayName]?.map((el, i) => {
						return (
							<li key={i}>
								<span>{el}</span>
								<div onClick={() => handleDelete(i)}>
									<DeleteButton />
								</div>
							</li>
						);
					})
				) : (
					<li className='opacity-50'>Add an item...</li>
				)}
				<li className={show ? '' : 'hidden'}>
					<input
						type='text'
						name={arrayName}
						placeholder={'Add an item...'}
						onChange={handleFormChange}
						className='input input-bordered w-full max-w-xs'
					/>
					<button
						onClick={() => {
							setShow(!show);
							setFormData({
								[arrayName]: '',
							});
						}}
					>
						<SmallCheckButton />
					</button>
				</li>
			</ul>
		</form>
	);
}

export default ArrayInput;
