import React from 'react';
import useAuth from '../auth/useAuth';
import axios from 'axios';
import useStudents from '../contexts/useStudents';

function Lesson() {
	const { user } = useAuth();
	const { students } = useStudents();
	const [lessons, setLessons] = React.useState([]);

	React.useEffect(() => {
		(async () => {
			try {
				const response = await axios({
					method: 'GET',
					data: user._id,
					url: 'http://localhost:5000/lessons',
					withCredentials: true,
				});

				setLessons(response.data);
			} catch (err) {
				console.log(err);
			}
		})();
	}, [user._id]);

	function returnStudent(id) {
		let filtered = students.filter(element => element._id === id);
		return filtered[0];
	}

	const lessonArr = lessons.map((el, i) => {
		let student = returnStudent(el);
		return <div key={i}>{student.name}</div>;
	});

	return <div>{lessonArr}</div>;
}

export default Lesson;
