import React from 'react';
import axios from 'axios';
import useAuth from '../auth/useAuth';

function UserAccount() {
	const { user } = useAuth();
	const [editMode, setEditMode] = React.useState(false);
	const [msg, setMsg] = React.useState({
		text: '',
		success: false,
	});
	const [formData, setFormData] = React.useState({});

	React.useEffect(() => {
		if (user) {
			setFormData({
				userName: user.userName,
				email: user.email,
			});
		}
	}, [user]);

	function handleFormChange(event) {
		const { name, value, type, checked } = event.target;
		setFormData(prevformData => ({
			...prevformData,
			[name]: type === 'checkbox' ? checked : value,
		}));
	}

	const handleSubmit = async event => {
		event.preventDefault();
		try {
			const response = await axios({
				method: 'PUT',
				data: {
					id: user._id,
					userName: formData.userName,
					email: formData.email,
				},
				url: 'http://localhost:5000/updateUser',
				withCredentials: true,
			});
			setMsg({
				text: response.data.message.msgBody,
				success: true,
			});
		} catch (err) {
			console.log(err);
			setMsg({
				text: err.response.data.message.msgBody,
				success: false,
			});
		}
	};

	return (
		<div>
			<div className='p-3 mb-3'>
				<h1 className='text-3xl text-center lg:text-start'>Account Settings</h1>
			</div>
			{user && (
				<form
					onSubmit={handleSubmit}
					className='bg-base-200 p-5 rounded mx-3 custom-80vh text-xl relative pb-20'
				>
					<div className='flex flex-col gap-5'>
						<div className='flex gap-3'>
							<div className='avatar'>
								<div className='w-24 rounded'>
									<img
										src='https://placeimg.com/192/192/people'
										alt='user profile'
									/>
								</div>
							</div>
							<div className='flex flex-col justify-evenly w-full'>
								{editMode ? (
									<input
										type='text'
										name='name'
										placeholder={user.name}
										onChange={handleFormChange}
										className='input input-bordered w-full max-w-xs'
									/>
								) : (
									<h3 className='text-2xl'>{user.name}</h3>
								)}
								{editMode ? (
									<input
										type='text'
										name='instrument'
										placeholder={user.instrument}
										onChange={handleFormChange}
										className='input input-bordered w-full max-w-xs'
									/>
								) : (
									<span>{user.instrument}</span>
								)}
							</div>
						</div>
						<div className='divider'></div>
						{lesson && (
							<div className='flex justify-evenly md:w-full text-center'>
								<div className='flex flex-col justify-evenly'>
									<h5 className='font-semibold'>Next Lesson</h5>
									<span>{`${lesson.date.weekday}, ${lesson.date.date}`}</span>
									<span className='text-lg'>{`${lesson.date.start} - ${lesson.date.end}`}</span>
								</div>
							</div>
						)}
						<div className='text-lg flex flex-col p-2 rounded-xl gap-3'>
							<div>
								<h5 className='font-semibold'>Repertoire</h5>
								<ul className='list-disc list-inside'>
									<li>{user.name}</li>
								</ul>
							</div>
							<div>
								<h5 className='font-semibold'>Concepts</h5>
								<ul className='list-disc list-inside'>
									<li>{user.name}</li>
								</ul>
							</div>
							<div>
								<h5 className='font-semibold'>Contact Information</h5>
								<ul className='list-disc list-inside'>
									{editMode ? (
										<input
											type='tel'
											pattern='[0-9]{3}-[0-9]{3}-[0-9]{4}'
											name='phone'
											placeholder={user.phone}
											onChange={handleFormChange}
											className='input input-bordered w-full max-w-xs'
										/>
									) : (
										<li>Phone: {user.phone}</li>
									)}
									{editMode ? (
										<input
											type='text'
											name='email'
											placeholder={user.email}
											onChange={handleFormChange}
											className='input input-bordered w-full max-w-xs'
										/>
									) : (
										<li>Email: {user.email}</li>
									)}
									{editMode ? (
										<input
											type='text'
											name='primaryContact'
											placeholder={user.primaryContact}
											onChange={handleFormChange}
											className='input input-bordered w-full max-w-xs'
										/>
									) : (
										<li>Primary Contact: {user.primaryContact}</li>
									)}
								</ul>
							</div>
							<div>
								<h5 className='font-semibold'>Status</h5>
								<ul className='list-disc list-inside'>
									{editMode ? (
										<select
											className='select select-bordered w-full max-w-xs'
											id='user'
											value={formData.user}
											onChange={handleFormChange}
											name='user'
										>
											<option value='Active'>Active</option>
											<option value='Inactive'>Inactive</option>
											<option value='Archived'>Archived</option>
										</select>
									) : (
										<li>{user.status}</li>
									)}
								</ul>
							</div>
						</div>
					</div>
					<div
						className={
							msg.success
								? 'text-success text-center'
								: 'text-error text-center'
						}
					>
						{msg ? msg.text : ''}
					</div>
					<div className='absolute left-4 bottom-4 flex gap-3'>
						{!editMode && (
							<div
								className='btn btn-ghost hover:bg-error border-base-300'
								onClick={() => setEditMode(!editMode)}
							>
								edit
							</div>
						)}
						{editMode && (
							<button className='btn btn-ghost hover:bg-error border-base-300'>
								Save
							</button>
						)}
						{editMode && (
							<div
								className='btn btn-ghost hover:bg-error border-base-300'
								onClick={() => setEditMode(!editMode)}
							>
								cancel
							</div>
						)}
					</div>

					<div className='absolute right-4 bottom-4'>
						<div
							className='btn btn-ghost btn-square hover:bg-error border border-base-300'
							onClick={() => deleteLesson(user._id)}
						>
							<svg
								xmlns='http://www.w3.org/2000/svg'
								fill='none'
								viewBox='0 0 24 24'
								strokeWidth={1.5}
								stroke='currentColor'
								className='w-6 h-6'
							>
								<path
