import React from 'react';

function StudentProfile({ currentStudent, setCurrentStudent }) {
	return (
		<div>
			{currentStudent ? (
				<div className='bg-base-200 p-5 rounded mx-5'>
					<div className='avatar'>
						<div className='w-24 rounded'>
							<img
								src='https://placeimg.com/192/192/people'
								alt='student profile'
							/>
						</div>
					</div>
					<div>
						<h3 className='text-2xl'>{currentStudent.name}</h3>
						<span className='text-sm'>
							Primary Contact: {currentStudent.name}
						</span>
					</div>
				</div>
			) : (
				<span>No Student Selected</span>
			)}
		</div>
	);
}

export default StudentProfile;
