import React from 'react';
import axios from 'axios';
import useAuth from '../auth/useAuth';

function CreateLesson() {
	const { user } = useAuth();

	const [msg, setMsg] = React.useState({
		text: '',
		success: false,
	});

	const [formData, setFormData] = React.useState({
		teacher: '',
		student: '',
		date: '',
	});

	function handleFormChange(event) {
		const { name, value, type, checked } = event.target;
		setFormData(prevformData => ({
			...prevformData,
			[name]: type === 'checkbox' ? checked : value,
		}));
	}

	const handleSubmit = async event => {
		event.preventDefault();
		console.log(formData, 'New Student Attempt Sent');
		try {
			const response = await axios({
				method: 'POST',
				data: {
					teacher: user._id,
					student: '',
					date: '',
				},
				url: 'http://localhost:5000/lessons/createLesson',
				withCredentials: true,
			});
			console.log('From Server:', response);
			setMsg({
				text: response.data.message.msgBody,
				success: true,
			});
			event.target.reset();
		} catch (err) {
			setMsg({
				text: err.response.data.message.msgBody,
				success: false,
			});
			console.log(err.response);
		}
	};

	const gameModes = ['Elimination', 'Domination', 'Regicide', 'CaptureTheFlag'];

	const selectStudent = gameModes.map((mode, i) => {
		return (
			<div key={i}>
				<input
					type='radio'
					id={mode}
					name='gameMode'
					value={mode}
					checked={gameData.gameconfig.gameMode === { mode }}
					onChange={handleConfigChange}
					className='hidden'
				/>
				<label
					htmlFor={mode}
					className={
						gameData.gameconfig.gameMode === mode
							? 'custom-bg-gray w-fit rounded-lg px-5 text-2xl text-white hover:cursor-pointer'
							: 'w-fit rounded-lg px-5 text-2xl hover:cursor-pointer'
					}
				>
					{mode === 'captureTheFlag' ? 'capture the flag' : mode}
				</label>
			</div>
		);
	});

	return (
		<div>
			<label htmlFor='createStudent-modal' className='btn btn-square'>
				<svg
					xmlns='http://www.w3.org/2000/svg'
					fill='none'
					viewBox='0 0 24 24'
					strokeWidth={1.5}
					stroke='currentColor'
					className='w-6 h-6'
				>
					<path
						strokeLinecap='round'
						strokeLinejoin='round'
						d='M12 4.5v15m7.5-7.5h-15'
					/>
				</svg>
			</label>
			<input
				type='checkbox'
				id='createStudent-modal'
				className='modal-toggle'
			/>
			<label htmlFor='createStudent-modal' className='modal cursor-pointer'>
				<label>
					<section className='flex flex-col items-center p-10'>
						<div className='card w-96 shadow-xl bg-neutral'>
							<div className='card-body'>
								<h1 className='card-title self-center mb-4 text-white'>
									New Lesson
								</h1>
								<form onSubmit={handleSubmit} className='flex flex-col gap-2'>
									{selectStudent}
									<div className='card-actions justify-center mt-4'>
										<button className='btn btn-primary'>
											Create New Student
										</button>
									</div>
								</form>
								<div
									className={
										msg.success
											? 'text-success text-center'
											: 'text-warning text-center'
									}
								>
									{msg ? msg.text : ''}
								</div>
							</div>
						</div>
					</section>
				</label>
			</label>
		</div>
	);
}

export default CreateLesson;
