import React from 'react';
import useStudents from '../contexts/useStudents';
import useLessons from '../contexts/useLessons';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';

function StudentProfile() {
	const { students, getStudents, setGetStudents } = useStudents();
	const { lessons } = useLessons();
	const navigate = useNavigate();

	const [selectedStudent, setSelectedStudent] = React.useState({});
	const [editMode, setEditMode] = React.useState(false);

	const { id } = useParams();
	React.useEffect(() => {
		setSelectedStudent(students.find(student => student._id === id));
	}, [id, students]);

	async function deleteLesson(studentID) {
		if (window.confirm('Are you sure you want to delete this student?')) {
			try {
				const response = await axios({
					method: 'DELETE',
					data: { id: studentID },
					url: 'http://localhost:5000/students/delete',
					withCredentials: true,
				});
				console.log('From Server:', response);
				setGetStudents(!getStudents);
				navigate('/students');
			} catch (err) {
				console.log(err.response);
			}
		}
	}

	let lesson;
	if (lessons && selectedStudent) {
		lesson = lessons.find(lesson => lesson.student === selectedStudent._id);
	}

	return (
		<div>
			<label className='label cursor-pointer'>
				<span className='label-text'>Edit Mode</span>
				<input type='checkbox' className='toggle' />
			</label>
			{selectedStudent && (
				<div className='bg-base-200 p-5 rounded mx-3 custom-80vh text-xl relative'>
					<div className='flex flex-col gap-5'>
						<div className='flex gap-3'>
							<div className='avatar'>
								<div className='w-24 rounded'>
									<img
										src='https://placeimg.com/192/192/people'
										alt='student profile'
									/>
								</div>
							</div>
							<div className='flex flex-col justify-evenly w-full'>
								<h3 className='text-2xl'>{selectedStudent.name}</h3>
								<span>{selectedStudent.instrument}</span>
							</div>
						</div>
						<div className='divider'></div>
						{lesson && (
							<div className='flex justify-evenly md:w-full text-center'>
								<div className='flex flex-col justify-evenly'>
									<h5 className='font-semibold'>Next Lesson</h5>
									<span>{`${lesson.date.weekday}, ${lesson.date.date}`}</span>
									<span className='text-lg'>{`${lesson.date.start} - ${lesson.date.end}`}</span>
								</div>
							</div>
						)}
						<div className='text-lg flex flex-col p-2 rounded-xl'>
							<div>
								<h5 className='font-semibold'>Repertoire</h5>
								<ul className='list-disc list-inside'>
									<li>{selectedStudent.name}</li>
								</ul>
								<h5 className='font-semibold'>Concepts</h5>
								<ul className='list-disc list-inside'>
									<li>{selectedStudent.name}</li>
								</ul>
								<h5 className='font-semibold'>Contact Information</h5>
								<ul className='list-disc list-inside'>
									<li>Name: {selectedStudent.name}</li>
									<li>Phone: {selectedStudent.name}</li>
									<li>Email: {selectedStudent.name}</li>
									<li>Primary Contact: {selectedStudent.name}</li>
								</ul>
								<h5 className='font-semibold'>Status</h5>
								<ul className='list-disc list-inside'>
									<li>{selectedStudent.name}</li>
								</ul>
							</div>
						</div>
					</div>
					<div className='absolute right-4 bottom-4'>
						<button
							className='btn btn-ghost btn-square hover:bg-error border border-base-300'
							onClick={() => deleteLesson(selectedStudent._id)}
						>
							<svg
								xmlns='http://www.w3.org/2000/svg'
								fill='none'
								viewBox='0 0 24 24'
								strokeWidth={1.5}
								stroke='currentColor'
								className='w-6 h-6'
							>
								<path
									strokeLinecap='round'
									strokeLinejoin='round'
									d='M14.74 9l-.346 9m-4.788 0L9.26 9m9.968-3.21c.342.052.682.107 1.022.166m-1.022-.165L18.16 19.673a2.25 2.25 0 01-2.244 2.077H8.084a2.25 2.25 0 01-2.244-2.077L4.772 5.79m14.456 0a48.108 48.108 0 00-3.478-.397m-12 .562c.34-.059.68-.114 1.022-.165m0 0a48.11 48.11 0 013.478-.397m7.5 0v-.916c0-1.18-.91-2.164-2.09-2.201a51.964 51.964 0 00-3.32 0c-1.18.037-2.09 1.022-2.09 2.201v.916m7.5 0a48.667 48.667 0 00-7.5 0'
								/>
							</svg>
						</button>
					</div>
				</div>
			)}
		</div>
	);
}

export default StudentProfile;
