import React from 'react';
import SmallAddButton from '../components/SmallAddButton';
import useStringHook from '../hooks/useStringHook';

function ArrayInput({ student, arrayName, handleFormChange }) {
	const [show, setShow] = React.useState(false);
	const { capitolizeFirst } = useStringHook();

	React.useEffect(() => {
		if (student) {
			setFormData({
				name: student.name,
				age: student.age,
				repertoire: student.repertoire,
				concepts: student.concepts,
				phone: student.phone,
				email: student.email,
				primaryContact: student.primaryContact,
				instrument: student.instrument,
				status: student.status,
			});
		}
	}, [student]);

	async function deleteLesson(studentID) {
		if (window.confirm('Are you sure you want to delete this student?')) {
			try {
				const response = await axios({
					method: 'DELETE',
					data: { id: studentID },
					url: 'http://localhost:5000/students/delete',
					withCredentials: true,
				});
				console.log('From Server:', response);
				setGetStudents(!getStudents);
				navigate('/students');
			} catch (err) {
				console.log(err.response);
			}
		}
	}

	function handleFormChange(event) {
		const { name, value, type, checked } = event.target;
		setFormData(prevformData => ({
			...prevformData,
			[name]: type === 'checkbox' ? checked : value,
		}));
	}

	const handleSubmit = async event => {
		console.log(formData);
		event.preventDefault();
		try {
			const response = await axios({
				method: 'PUT',
				data: {
					id: student._id,
					name: formData.name,
					age: formData.age,
					repertoire: student.repertoire,
					concepts: student.concepts,
					phone: formData.phone,
					email: formData.email,
					primaryContact: formData.primaryContact,
					instrument: formData.instrument,
					status: formData.status,
				},
				url: 'http://localhost:5000/students/updateStudent',
				withCredentials: true,
			});
			setMsg(
				{
					text: response.data.message.msgBody,
					success: true,
				},
				setClearMsg(!clearMsg)
			);
			setEditMode(!editMode);
			setGetStudents(!getStudents);
		} catch (err) {
			console.log(err);
			setMsg(
				{
					text: err.response.data.message.msgBody,
					success: false,
				},
				setClearMsg(!clearMsg)
			);
		}
	};

	return (
		<div>
			<div className='flex gap-3 items-center'>
				<h5 className='font-semibold'>{capitolizeFirst(arrayName)}</h5>
				<div onClick={() => setShow(!show)}>
					<SmallAddButton />
				</div>
			</div>
			<ul className='list-disc list-inside'>
				{student[arrayName]?.length > 0 ? (
					student[arrayName]?.map((el, i) => {
						return <li key={i}>{el}</li>;
					})
				) : (
					<li>Add an item...</li>
				)}
				<li className={show ? '' : 'hidden'}>
					<input
						type='text'
						name={arrayName}
						placeholder={'Add an item...'}
						onChange={handleFormChange}
						className='input input-bordered w-full max-w-xs'
					/>
					<div>
						<SmallAddButton />
					</div>
				</li>
			</ul>
		</div>
	);
}

export default ArrayInput;
