import React from 'react';
import axios from 'axios';
import useAuth from '../auth/useAuth';

function CreateLesson() {
	const { user } = useAuth();

	const [msg, setMsg] = React.useState({
		text: '',
		success: false,
	});

	const [formData, setFormData] = React.useState({
		student: '',
		date: '',
	});

	function handleFormChange(event) {
		const { name, value, type, checked } = event.target;
		setFormData(prevformData => ({
			...prevformData,
			[name]: type === 'checkbox' ? checked : value,
		}));
	}

	const handleSubmit = async event => {
		event.preventDefault();
		console.log(formData, 'New Student Attempt Sent');
		try {
			const response = await axios({
				method: 'POST',
				data: {
					teacher: user._id,
					student: '',
					date: '',
					range: '',
				},
				url: 'http://localhost:5000/lessons/createLesson',
				withCredentials: true,
			});
			console.log('From Server:', response);
			setMsg({
				text: response.data.message.msgBody,
				success: true,
			});
			event.target.reset();
		} catch (err) {
			setMsg({
				text: err.response.data.message.msgBody,
				success: false,
			});
			console.log(err.response);
		}
	};

	const [students, setStudents] = React.useState([]);

	React.useEffect(() => {
		(async () => {
			try {
				const response = await axios({
					method: 'GET',
					data: user._id,
					url: 'http://localhost:5000/students',
					withCredentials: true,
				});
				setStudents(response.data.sort((a, b) => (a.name > b.name ? 1 : -1)));
			} catch (err) {
				console.log(err);
			}
		})();
	}, [user._id]);

	const selectStudent = students.map((el, i) => {
		return <option key={i}>{el.name}</option>;
	});

	return (
		<div>
			<label htmlFor='createStudent-modal' className='btn btn-square'>
				<svg
					xmlns='http://www.w3.org/2000/svg'
					fill='none'
					viewBox='0 0 24 24'
					strokeWidth={1.5}
					stroke='currentColor'
					className='w-6 h-6'
				>
					<path
						strokeLinecap='round'
						strokeLinejoin='round'
						d='M12 4.5v15m7.5-7.5h-15'
					/>
				</svg>
			</label>
			<input
				type='checkbox'
				id='createStudent-modal'
				className='modal-toggle'
			/>
			<label htmlFor='createStudent-modal' className='modal cursor-pointer'>
				<label>
					<section className='flex flex-col items-center p-10'>
						<div className='card w-96 shadow-xl bg-base-100'>
							<div className='card-body'>
								<form
									onSubmit={handleSubmit}
									className='flex flex-col gap-2 form-control'
								>
									<label className='label flex flex-col gap-2'>
										<span className=''>Student:</span>
										<select
											className='select select-bordered w-full max-w-xs'
											id='selectStudent'
											value={formData.student}
											onChange={handleFormChange}
											name='selectStudent'
										>
											{selectStudent}
										</select>
									</label>
									<label className='label flex flex-col gap-2'>
										<span className=''>Date:</span>
										<input
											type='datetime-local'
											name='date'
											value={formData.date}
											onChange={handleFormChange}
											className='input input-bordered w-full max-w-xs'
										/>
									</label>
									<label className='label flex flex-col gap-2'>
										<span className=''>Length in minutes:</span>
										<input
											type='range'
											min='0'
											max='5'
											value={formData.range}
											onChange={handleFormChange}
											className='range'
											step='1'
										/>
									</label>
									<div className='w-full flex justify-between text-xs px-2'>
										<span>15</span>
										<span>30</span>
										<span>45</span>
										<span>60</span>
										<span>1:15</span>
										<span>1:30</span>
									</div>
									<div className='card-actions justify-center mt-4'>
										<button className='btn btn-primary'>
											Create New Lesson
										</button>
									</div>
								</form>
								<div
									className={
										msg.success
											? 'text-success text-center'
											: 'text-warning text-center'
									}
								>
									{msg ? msg.text : ''}
								</div>
							</div>
						</div>
					</section>
				</label>
			</label>
		</div>
	);
}

export default CreateLesson;
